import React, { useState } from 'react';
import NavTabs from './NavTabs';
import Home from './pages/Home';
import About from './pages/About';
import Blog from './pages/Blog';
import Contact from './pages/Contact';

export default function PortfolioContainer() {
    const [currentPage, setCurrentPage] = useState('Home');

    // TODO: Add a comment describing the functionality of this method
    //creating a conditional operator for the pages that were imported to render which page they are on/select in the navtabs
    const renderPage = () => {
        if (currentPage === 'Home') {
            return <Home />;
        }
        if (currentPage === 'About') {
            return <About />;
        }
        if (currentPage === 'Blog') {
            return <Blog />;
        }
        return <Contact />;
    };

    const handlePageChange = (page) => setCurrentPage(page);

    return (
        <div>
            {/* // TODO: Add a comment describing what we are passing as props */}
            // calling the function created from the imported page NavTabs and returning the rendered page as the prop based on which nav item they select
            <NavTabs currentPage={currentPage} handlePageChange={handlePageChange} />
            {/* // TODO: Add a comment explaining what is happening on the following line */}

            {renderPage()}
        </div>
    );
}
